mapper.update:
update cus_org_link
set DEL_FLAG = '1'
where BAS_ORG_ID = #{basOrgId,jdbcType=VARCHAR} and cus_id =#{cusId}

mapper.insert:
insert into cus_org_link (LINK_ID, CUS_ID, BAS_ORG_ID,create_id,create_date,
update_id,update_date,state
  )
values (#{linkId,jdbcType=VARCHAR}, #{cusId,jdbcType=VARCHAR}, #{basOrgId,jdbcType=VARCHAR},#{createId},
#{createDate},#{updateId},#{updateDate},#{state}
  )
